let x,y,progressSection=document.querySelector(".progressSection"),progressBar=document.querySelector(".progressBar"),progressNum=document.querySelector(".progressNum");function updateProgressBar(){progressBar.style.height=`${getScrollPercentage()}%`,progressNum.innerText=`${Math.ceil(getScrollPercentage())}%`,requestAnimationFrame(updateProgressBar)}function getScrollPercentage(){return window.scrollY/(document.body.scrollHeight-window.innerHeight)*100}updateProgressBar();